import {Component} from '@angular/core';
import {Todo, TodoService} from "./todo.service";
import {Observable} from "rxjs";
import { filter, map } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  template: `
    <div class="title">
      <h1>
        A list of TODOs
      </h1>
    </div>
    <div class="list">
      <label for="search">Search...</label>
      <input #inputSearch id="search" type="text" (keyup)="changeFilter(inputSearch.value)">
      <app-progress-bar *ngIf="showProgressBar"></app-progress-bar>
      <app-todo-item *ngFor="let todo of todos$ | async | todo-filter: inputSearch.value" [item]="todo"></app-todo-item>
    </div>
  `,
  styleUrls: ['app.component.scss']
})
export class AppComponent {

  readonly todos$: Observable<Todo[]>;
  showProgressBar: boolean = true;
  filterText: string = '';

  constructor(todoService: TodoService) {
    this.todos$ = todoService.getAll();
    this.todos$.subscribe(() => this.showProgressBar = false)
  }

  changeFilter(text: string){
    this.filterText = text;
    console.log(this.filterText);
  }
}
